import React from 'react';

class Timer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toLocaleTimeString()
        };

    }

    componentDidMount() {
        setInterval(() => {
            this.setState({ date: new Date() });
        }, 1000);
    }

