import React from 'react';

class Timer extends React.Component {
    state = {
        date: new Date()
    }

    renderTime() {
        setInterval(() => {
            this.props.date = new Date();
        }, 1000);
    }

    render() {
        return (
            <div className="display-time">
                <h2>{this.props.date.toLocalTimeString()}</h2>
                {this.renderTime()}
                <button>New Booking</button>
            </div>
        )
    }

}

export default Timer;
