import React from 'react';

class Timer extends React.Component {
    state = {
        date: new Date()
    };

    renderTime() {
        setInterval(() => {
            this.setState({ date: new Date() });
        }, 1000);
    }

    render() {
        return (
            <div className="display-time">
            
                <h2>{this.props.date.toLocaleTimeString()}</h2>
                {this.renderTime()}
            </div>
        )
    }

}

export default Timer;
