import React from 'react';

class Timer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            time: new Date().toLocaleTimeString()
        };
    }

    // state = {
    //     date: new Date()
    // };

    // renderTime() {
    //     setInterval(() => {
    //         this.setState({ date: new Date() });
    //     }, 1000);
    // }

    render() {
        return (
            <div className="display-time">
                <h1>Current Time</h1>
                <h2>{this.props.time}</h2>
                {this.renderTime()}
            </div>
        )
    }

}

export default Timer;
